mapper.select:
select
*
from collection

     and id= #{id}
     and article_id=#{articleId}
     and c_id=#{cId}

order by id desc

select
    article.*
from collection
         left join article on article.id=collection.article_id

    collection.c_id=#{cId} 
     and collection.name=#{category}
     and article.journal=#{journal}
     and article.type=#{type}
     and article.name like concat('%', #{name}, '%')
     and article.keywords like concat('%', #{keywords}, '%')
     and article.author like concat('%', #{author}, '%')
